import React, { useState } from 'react'

import './ExpenseForm.css'

const ExpenseForm = () => {
  // const [userTitle, setUserTitle] = useState('')
  // const [userAmount, setUserAmount] = useState('')
  // const [userDate, setUserDate] = useState('')

  const [userInput, setUserInput] = useState({
    userTitle: '',
    userAmount: '',
    userAmount: '',
  })

  const titleChangeHandler = (event) => {
    // setUserTitle(event.target.value)

    // setUserInput({
    //   ...userInput,
    //   userTitle: event.target.value,
    // })

    // safer way to update state whenever you depend on previous state since React
    // garantees that previous state is the lastest before updating

    setUserInput((prevState) => {
      return { ...prevState, userTitle: event.target.value }
    })
  }
  const amountChangeHandler = (event) => {
    setUserInput({
      ...userInput,
      userAmount: event.target.value,
    })
  }
  const dateChangeHandler = (event) => {
    setUserInput({
      ...userInput,
      userDate: event.target.value,
    })
  }

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>{userInput.userTitle}</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>

        <div className="new-expense__control">
          <label>{userInput.userAmount}</label>
          <input
            type="number"
            onChange={amountChangeHandler}
            min="0.01"
            step="0.01"
          />
        </div>

        <div className="new-expense__control">
          <label>{userInput.userDate}</label>
          <input
            type="date"
            onChange={dateChangeHandler}
            min="2021-01-01"
            max="2024-12-31"
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  )
}

export default ExpenseForm
